    (
        word_name
      , eng_word_name
      , word_desc
      , reg_user_id
      , mdfr_user_id
    )
VALUES
    (
        #{wordName}
      , #{engWordName}
      , #{wordDesc}
      , 'admin'
      , 'admin'
    )

mapper.select:
SELECT
       COUNT(*) AS cnt
FROM mgt.cmm_std_word


        OR word_name = #{wordName}


        OR eng_word_name = #{engWordName}
